const {
    success,
    error
} = require("../helpers/apiResponse");
const {
    getPagingData,
    getPagination
} = require("../helpers/common");
const db = require("../models");
const Op = db.Sequelize.Op;
const TripService = require("../services/trip.service");

/*
 * @lists Method
 *  - Get a list of all trips
 */

exports.lists = (req, res) => {

    const {
        page,
        size,
        status,
        driverId,
        riderId
    } = req.query;
    const {
        limit,
        offset
    } = getPagination(page, size);

    var condition = status || driverId || riderId ? {
        status: {
            [Op.like]: `%${status}%`
        },
        driverId: {
            [Op.eq]: driverId
        },
        riderId: {
            [Op.eq]: riderId
        }
    } : null;

    var params = {
        where: condition,
        limit,
        offset
    };

    TripService.all(params)
        .then(data => {
            if (data) {
                const response = getPagingData(data, page, limit);
                return success(res, "Get a list of all trips", 200, response);
            } else {
                return error(res, "No Trips found", 404);
            }

        })
        .catch(err => {
            return error(res, err.message || "Some error occurred while getting the Trips.", 500);

        });
};

